//SPDX-License-Identifier: MIT

import { DateTime } from "../external/DateTime.sol";

import { StringConversionUtils } from "./StringConversionUtils.sol";

pragma solidity ^0.8.18;

library DateParsing {

    using StringConversionUtils for string;

    /**
     * @notice Iterates through every character in the date string and splits the string at each dash, "T", or colon. Function will revert
     * if there are not 6 substrings formed from the split. The substrings are then converted to uints and passed to the DateTime lib
     * to get the unix timestamp. This function is SPECIFIC TO THE DATE FORMAT YYYY-MM-DDTHH:MM:SS or YYYY-MM-DDTHH:MM:SS.SSSZ, not suitable for 
     * use with other date formats. It skips the milliseconds and timezone offset, as they are not present in all date strings. It returns UTC 
     * timestamps.
     *
     * @param _dateString       Date string to be converted to a UTC timestamp
     */
    function _dateStringToTimestamp(string memory _dateString) internal pure returns (uint256 utcTimestamp) {
        string[6] memory extractedStrings;
        uint256 breakCounter;
        uint256 lastBreak;
        uint256 lastIndex = bytes(_dateString).length;
        for (uint256 i = 0; i < lastIndex; i++) {
            if (
                bytes(_dateString)[i] == 0x2d       // dash (-)
                || bytes(_dateString)[i] == 0x3a    // colon (:)
                || bytes(_dateString)[i] == 0x54    // T
                || bytes(_dateString)[i] == 0x20    // space
            ) {
                extractedStrings[breakCounter] = _dateString.substring(lastBreak, i);
                lastBreak = i + 1;
                breakCounter++;
            }

            if (bytes(_dateString)[i] == 0x2e) {    // dot (.)
                lastIndex = i;
                break;
            }
        }
        // Add last substring to array
        extractedStrings[breakCounter] = _dateString.substring(lastBreak, lastIndex);

        // Check that exactly 6 substrings were found (string is split at 5 different places)
        require(breakCounter == 5, "Invalid date string");

        utcTimestamp = DateTime.timestampFromDateTime(
            extractedStrings[0].stringToUint(0),    // year
            extractedStrings[1].stringToUint(0),    // month
            extractedStrings[2].stringToUint(0),    // day
            extractedStrings[3].stringToUint(0),    // hour
            extractedStrings[4].stringToUint(0),    // minute
            extractedStrings[5].stringToUint(0)     // second
        );
    }
}

contracts/lib/StringConversionUtils.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.18;

// Building on zk-email's StringUtils library we add the ability to handle decimals when
// converting from string to Uint
library StringConversionUtils {

    /**
     * @notice Function that parses numbers returned as strings including floating point numbers. Returned floating point
     * numbers are to have the desired amount of decimal specified. If the stringified version of the floating point
     * number has more decimal places than desired then the function will revert in order to be maximally safe. If
     * the returned number has multiple floating points then the function will revert.
     *
     * Examples: _s = "12.34", _expectedDecimals = 6 => 12340000
     *           _s = "12.34", _expectedDecimals = 2 => 1234
     *           _s = "12.34", _expectedDecimals = 1 => REVERT (we never want loss of precision only addition)
     *           _s = "12.34.56", _expectedDecimals = 6 => REVERT (Invalid number)
     *
     * @param _s                    String being processed
     * @param _desiredDecimals      Desired amount of decimal places
     */
    function stringToUint(string memory _s, uint256 _desiredDecimals) internal pure returns (uint256) {
        return stringToUint(_s, 0x2E, _desiredDecimals);
    }

    function stringToUint(
        string memory _s,
        bytes1 _decimalCharacter,
        uint256 _desiredDecimals
    )
        internal
        pure
        returns (uint256)
    {
        bytes memory b = bytes(_s);

        uint256 result = 0;
        uint256 decimalPlaces = 0;

        bool decimals = false;
        for (uint256 i = 0; i < b.length; i++) {
            if (b[i] >= 0x30 && b[i] <= 0x39) {
                result = result * 10 + (uint256(uint8(b[i])) - 48);
            }

            if (decimals) {
                decimalPlaces++;
            }

            if (b[i] == _decimalCharacter) {
                require(decimals == false, "String has multiple decimals");
                decimals = true;
            }
        }

        require(decimalPlaces <= _desiredDecimals, "String has too many decimal places");
        return result * (10 ** (_desiredDecimals - decimalPlaces));
    }

    /**
     * @notice Function that returns a substring from _startIndex to _endIndex (non-inclusive).
     *
     * @param _str           String being processed
     * @param _startIndex    Index to start parsing from
     * @param _endIndex      Index to stop parsing at (index not included in result)
     */
    function substring(string memory _str, uint _startIndex, uint _endIndex) internal pure returns (string memory ) {
        bytes memory strBytes = bytes(_str);
        bytes memory result = new bytes(_endIndex-_startIndex);
        for(uint i = _startIndex; i < _endIndex; i++) {
            result[i-_startIndex] = strBytes[i];
        }
        return string(result);
    }

    function stringComparison(string memory _a, string memory _b) internal pure returns (bool) {
        return (keccak256(abi.encodePacked(_a)) == keccak256(abi.encodePacked(_b)));
    }
}

contracts/lib/ThresholdSigVerifierUtils..content:

//SPDX-License-Identifier: MIT

import { SignatureChecker } from "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import { ECDSA } from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

pragma solidity ^0.8.18;


// This library is used to verify that signatures meet the required threshold from accepted witnesses
library ThresholdSigVerifierUtils {

    using SignatureChecker for address;
    using ECDSA for bytes32;

    /**
     * Verifies that signatures meet the required threshold from accepted witnesses
     * 
     * @param _digest The message digest to verify (EIP-712 or pre-hashed for EIP-191)
     * @param _signatures Array of signatures (must have at least minWitnessSignatures)
     * @param _witnesses Array of accepted witness addresses
     * @param _reqThreshold The minimum number of witness signatures required
     * @return success Returns true if the threshold is met, otherwise reverts
     * @dev The digest should already be properly formatted:
     *      - For EIP-712: keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash))
     *      - For EIP-191: the message hash that will be signed with personal_sign
     *      SignatureChecker.isValidSignatureNow will handle both cases correctly
     */
    function verifyWitnessSignatures(
        bytes32 _digest,
        bytes[] memory _signatures,
        address[] memory _witnesses,
        uint256 _reqThreshold
    )
        internal
        view
        returns (bool success)
    {
        success = false;
        require(_reqThreshold > 0, "ThresholdSigVerifierUtils: req threshold must be > 0");
        require(_reqThreshold <= _signatures.length, "ThresholdSigVerifierUtils: req threshold exceeds signatures");
        require(_reqThreshold <= _witnesses.length, "ThresholdSigVerifierUtils: req threshold exceeds witnesses");

        // For EIP-712, the digest is already properly formatted
        // For EIP-191, we need to convert to Ethereum signed message hash
        // SignatureChecker handles both EIP-712 and EIP-191 signatures
        // We'll pass the digest directly and let SignatureChecker handle it

        // Track unique signers using an array
        address[] memory seenSigners = new address[](_witnesses.length);
        uint256 validWitnessSignatures = 0;

        // Check each signature to find which witness signed it
        for (uint256 i = 0; i < _signatures.length; i++) {
            // Check if any witness created this signature
            for (uint256 j = 0; j < _witnesses.length; j++) {
                if (_witnesses[j].isValidSignatureNow(_digest, _signatures[i])) {
                    // Check if we've already counted this witness
                    bool alreadySeen = false;
                    for (uint256 k = 0; k < validWitnessSignatures; k++) {
                        if (seenSigners[k] == _witnesses[j]) {
                            alreadySeen = true;
                            break;
                        }
                    }
                    if (!alreadySeen) {
                        seenSigners[validWitnessSignatures] = _witnesses[j];
                        validWitnessSignatures++;

                        // Early exit if threshold is met
                        if (validWitnessSignatures >= _reqThreshold) {
                            success = true;
                        }
                        break;
                    }
                }
            }
        }

        // Check threshold
        require(
            validWitnessSignatures >= _reqThreshold,
            "ThresholdSigVerifierUtils: Not enough valid witness signatures"
        );
    }
}

