contracts/unifiedVerifier/BaseUnifiedPay.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { AddressArrayUtils } from "../external/AddressArrayUtils.sol";
import { Bytes32ArrayUtils } from "../external/Bytes32ArrayUtils.sol";
import { IAttestationVerifier } from "../interfaces/IAttestationVerifier.sol";
import { INullifierRegistry } from "../interfaces/INullifierRegistry.sol";

/**
 * @title BaseUnifiedPaymentVerifier
 * @notice Base contract for unified payment verification that manages configuration for multiple payment methods.
 * 
 * This contract handles:
 * - Payment method configuration (timestamp buffers, zkTLS provider hashes)
 * - zkTLS attestation verification through pluggable attestation verifiers
 * - Support for multiple zkTLS proofs per payment method
 * 
 * @dev This is an abstract contract that must be inherited by concrete implementations.
 *      It replaces the previous BaseReclaimVerifier with a more flexible architecture.
 */
abstract contract BaseUnifiedPaymentVerifier is Ownable {

    using AddressArrayUtils for address[];
    using Bytes32ArrayUtils for bytes32[];

    /* ============ Constants ============ */

    uint256 internal constant PRECISE_UNIT = 1e18;

    /* ============ Structs ============ */

    struct PaymentMethodStore {
        bool initialized;
        uint256 timestampBuffer;
        mapping(bytes32 => bool) isProviderHash;
        bytes32[] providerHashes;
    }

    /* ============ Events ============ */

    event PaymentMethodAdded(bytes32 indexed paymentMethod, uint256 timestampBuffer);
    event PaymentMethodRemoved(bytes32 indexed paymentMethod);
    event TimestampBufferUpdated(bytes32 indexed paymentMethod, uint256 oldBuffer, uint256 newBuffer);
    event ProviderHashAdded(bytes32 indexed paymentMethod, bytes32 indexed providerHash);
    event ProviderHashRemoved(bytes32 indexed paymentMethod, bytes32 indexed providerHash);
    event AttestationVerifierUpdated(address indexed oldVerifier, address indexed newVerifier);

    /* ============ State Variables ============ */

    address public immutable orchestrator;
    INullifierRegistry public immutable nullifierRegistry;
    IAttestationVerifier public attestationVerifier;

    bytes32[] public paymentMethods;
    mapping(bytes32 => PaymentMethodStore) public store;

    /* ============ Modifiers ============ */

    /**
     * Modifier to ensure only escrow can call
     */
    modifier onlyOrchestrator() {
        require(msg.sender == orchestrator, "Only orchestrator can call");
        _;
    }

    /* ============ Constructor ============ */

    /**
     * @notice Initializes base payment verifier
     * @param _orchestrator The orchestrator contract address that will be used to verify payments
     * @param _nullifierRegistry The nullifier registry contract that will be used to prevent double-spends
     * @param _attestationVerifier The attestation verifier contract that will be used to verify attestation by the
     * offchain / ZK attestation service
     */
    constructor(
        address _orchestrator,
        INullifierRegistry _nullifierRegistry,
        IAttestationVerifier _attestationVerifier
    ) Ownable() {
        orchestrator = _orchestrator;
        nullifierRegistry = _nullifierRegistry;
        attestationVerifier = _attestationVerifier;
    }

    /* ============ External Functions ============ */

    /**
     * ONLY OWNER: Adds a new payment method with timestamp buffer and zkTLS provider hashes
     * @param _paymentMethod The payment method hash; Hash the payment method name in lowercase
     * @param _timestampBuffer Payment method-specific timestamp buffer in seconds
     * @param _providerHashes Array of zkTLS provider hashes to store
     */
    function addPaymentMethod(
        bytes32 _paymentMethod,
        uint256 _timestampBuffer,
        bytes32[] calldata _providerHashes
    ) external onlyOwner {
        require(!store[_paymentMethod].initialized, "UPV: Payment method already exists");
        require(_providerHashes.length > 0, "UPV: Invalid length");

        store[_paymentMethod].initialized = true;
        store[_paymentMethod].timestampBuffer = _timestampBuffer;
        paymentMethods.push(_paymentMethod);

        addProviderHashes(_paymentMethod, _providerHashes);

        emit PaymentMethodAdded(_paymentMethod, _timestampBuffer);
    }

    /**
     * ONLY OWNER: Removes a payment method and associated configuration
     * @param _paymentMethod The payment method to remove
     * @dev Only callable by owner
     */
    function removePaymentMethod(bytes32 _paymentMethod) external onlyOwner {
        require(store[_paymentMethod].initialized, "UPV: Payment method does not exist");

        // Remove all provider hashes
        bytes32[] memory providerHashes = store[_paymentMethod].providerHashes;
        for (uint256 i = 0; i < providerHashes.length; i++) {
            _removeProviderHash(_paymentMethod, providerHashes[i]);
        }

        // Remove payment method config
        delete store[_paymentMethod];

        // Remove from paymentMethods array
        paymentMethods.removeStorage(_paymentMethod);

        emit PaymentMethodRemoved(_paymentMethod);
    }

    /**
     * @notice Updates the attestation verifier contract
     * @param _newVerifier The new attestation verifier address
     * @dev Only callable by owner
     */
    function setAttestationVerifier(address _newVerifier) external onlyOwner {
        address oldVerifier = address(attestationVerifier);
        require(_newVerifier != address(0), "UPV: Invalid attestation verifier");
        require(_newVerifier != oldVerifier, "UPV: Same verifier");

        attestationVerifier = IAttestationVerifier(_newVerifier);
        emit AttestationVerifierUpdated(oldVerifier, _newVerifier);
    }

    /**
     * Updates the timestamp buffer for a payment method
     * @param _paymentMethod The payment method hash
     * @param _newTimestampBuffer The new timestamp buffer in seconds
     */
    function setTimestampBuffer(bytes32 _paymentMethod, uint256 _newTimestampBuffer) external onlyOwner {
        require(store[_paymentMethod].initialized, "UPV: Payment method does not exist");

        uint256 oldBuffer = store[_paymentMethod].timestampBuffer;
        store[_paymentMethod].timestampBuffer = _newTimestampBuffer;

        emit TimestampBufferUpdated(_paymentMethod, oldBuffer, _newTimestampBuffer);
    }

    /**
     * Batch adds provider hashes for a specific payment method
     * @param _paymentMethod The payment method hash
     * @param _providerHashes Array of provider hashes to add
     */
    function addProviderHashes(bytes32 _paymentMethod, bytes32[] calldata _providerHashes) public onlyOwner {
        require(store[_paymentMethod].initialized, "UPV: Payment method does not exist");
        require(_providerHashes.length > 0, "UPV: Invalid length");

        for (uint256 i = 0; i < _providerHashes.length; i++) {
            _addProviderHash(_paymentMethod, _providerHashes[i]);
        }
    }

    /**
     * Batch removes provider hashes for a specific payment method
     * @param _paymentMethod The payment method hash
     * @param _providerHashes Array of provider hashes to remove
     */
    function removeProviderHashes(bytes32 _paymentMethod, bytes32[] calldata _providerHashes) external onlyOwner {
        require(_providerHashes.length > 0, "UPV: Invalid length");

        for (uint256 i = 0; i < _providerHashes.length; i++) {
            _removeProviderHash(_paymentMethod, _providerHashes[i]);
        }
    }

    /* ============ View Functions ============ */

    function getPaymentMethods() external view returns (bytes32[] memory) {
        return paymentMethods;
    }

    function isProviderHash(bytes32 _paymentMethod, bytes32 _providerHash) external view returns (bool) {
        return store[_paymentMethod].isProviderHash[_providerHash];
    }

    function getTimestampBuffer(bytes32 _paymentMethod) external view returns (uint256) {
        require(store[_paymentMethod].initialized, "UPV: Payment method does not exist");
        return store[_paymentMethod].timestampBuffer;
    }

    function getProviderHashes(bytes32 _paymentMethod) external view returns (bytes32[] memory) {
        require(store[_paymentMethod].initialized, "UPV: Payment method does not exist");
        return store[_paymentMethod].providerHashes;
    }

    /* ============ Internal Functions ============ */

    /**
     * Validates and adds a nullifier to prevent double-spending
     * @param _nullifier The nullifier to add
     */
    function _validateAndAddNullifier(bytes32 _nullifier) internal {
        require(!nullifierRegistry.isNullified(_nullifier), "Nullifier has already been used");
        nullifierRegistry.addNullifier(_nullifier);
    }

    /**
     * Gets the payment method store for a given payment method
     * @param paymentMethod The payment method to get the store for
     * @return _store The payment method store
     */
    function _getPaymentMethodStore(
        bytes32 paymentMethod
    ) internal view returns (PaymentMethodStore storage _store) {
        _store = store[paymentMethod];

        require(_store.initialized, "UPV: Payment method does not exist");
        return _store;
    }

    /**
     * Calculates the release amount based on the actual payment amount and conversion rate.
     * Caps the release amount at the intent amount.
     * NOTES:
     * - Assumes that _conversionRate is not zero and is in the same precision as PRECISE_UNIT.
     * - Function might overflow if _paymentAmount is very very large.
     * 
     * @param _paymentAmount The actual payment amount.
     * @param _conversionRate The conversion rate of the deposit token to the fiat currency.
     * @param _intentAmount The max amount of tokens the offchain payer wants to take.
     * @return The release amount.
     */
    function _calculateReleaseAmount(uint256 _paymentAmount, uint256 _conversionRate, uint256 _intentAmount) internal pure returns (uint256) {
        // releaseAmount = paymentAmount / conversionRate
        uint256 releaseAmount = (_paymentAmount * PRECISE_UNIT) / _conversionRate;

        // Ensure release amount doesn't exceed the intent amount (cap at intent amount)
        if (releaseAmount > _intentAmount) {
            releaseAmount = _intentAmount;
        }

        return releaseAmount;
    }

    /* ============ Helper Internal Functions ============ */

    function _addProviderHash(bytes32 _paymentMethod, bytes32 _providerHash) internal {
        require(_providerHash != bytes32(0), "UPV: Invalid provider hash");
        require(!store[_paymentMethod].isProviderHash[_providerHash], "UPV: Provider hash already exists");

        store[_paymentMethod].isProviderHash[_providerHash] = true;
        store[_paymentMethod].providerHashes.push(_providerHash);
        emit ProviderHashAdded(_paymentMethod, _providerHash);
    }

    function _removeProviderHash(bytes32 _paymentMethod, bytes32 _providerHash) internal {
        require(store[_paymentMethod].isProviderHash[_providerHash], "UPV: Provider hash does not exist");

        store[_paymentMethod].isProviderHash[_providerHash] = false;
        store[_paymentMethod].providerHashes.removeStorage(_providerHash);
        emit ProviderHashRemoved(_paymentMethod, _providerHash);
    }

}

contracts/unifiedVerifier/SimpleAttestat.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ECDSA } from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import { IAttestationVerifier } from "../interfaces/IAttestationVerifier.sol";
import { ThresholdSigVerifierUtils } from "../lib/ThresholdSigVerifierUtils.sol";

/**
 * @title SimpleAttestationVerifier
 * @notice Verifies attestations from off-chain verification service with on-chain trust anchors
 * @dev Simplified model with one offchain attestation service and one zkTLS attestor
 * 
 * The verification flow:
 * 1. User runs the zkTLS protocol with the attestor to generate a zkTLS proof
 * 2. Off-chain service verifies zkTLS proofs and generates standardized attestation; Attestation is
 *    signed by the witness and includes metadata about verification
 * 3. On-chain verification checks witness signature AND validates trust anchors to ensure
 *    offchain verification integrity
 */
contract SimpleAttestationVerifier is IAttestationVerifier, Ownable {

    using ECDSA for bytes32;

    /* ============ Events ============ */

    event WitnessUpdated(address indexed oldWitness, address indexed newWitness);
    event ZktlsAttestorUpdated(address indexed oldAttestor, address indexed newAttestor);
    event RequireZktlsValidationUpdated(bool required);

    /* ============ Constants ============ */

    uint256 public constant MIN_WITNESS_SIGNATURES = 1;

    /* ============ State Variables ============ */

    // Single witness that signs standardized attestations for offchain attestation service
    address public witness;

    // Single zkTLS attestor that performed the zkTLS verification
    address public zktlsAttestor;

    /* ============ Constructor ============ */

    /**
     * @notice Initializes the attestation verifier
     * @param _witness Initial witness address (can be zero to set later)
     * @param _zktlsAttestor Initial zkTLS attestor address (can be zero to set later)
     */
    constructor(address _witness, address _zktlsAttestor) Ownable() {
        witness = _witness;
        zktlsAttestor = _zktlsAttestor;
    }

    /* ============ External Functions ============ */

    /**
     * @notice Verifies attestations and trust anchor from off-chain verification service
     * @param _digest The message digest to verify (EIP-712 formatted)
     * @param _sigs Array with single signature from the witness
     * @param _data Verification metadata including zkTLS attestor address if required
     * @return isValid True if attestation and trust anchor are valid
     * 
     * @dev The data parameter contains the zkTLS attestor address that performed the verification
     */
    function verify(
        bytes32 _digest,
        bytes[] calldata _sigs,
        bytes calldata _data
    ) external view override returns (bool isValid) {
        isValid = _verifyAttestation(_digest, _sigs);

        if (isValid) {
            isValid = _verifyTrustAnchor(_data);
        }

        return isValid;
    }

    /* ============ Governance Functions ============ */

    /**
     * @notice Updates the witness address
     * @param _newWitness New witness address
     */
    function setWitness(address _newWitness) external onlyOwner {
        require(_newWitness != address(0), "SimpleAttestationVerifier: Zero address");

        address oldWitness = witness;
        witness = _newWitness;

        emit WitnessUpdated(oldWitness, _newWitness);
    }

    /**
     * @notice Updates the zkTLS attestor address
     * @param _newAttestor New zkTLS attestor address
     */
    function setZktlsAttestor(address _newAttestor) external onlyOwner {
        require(_newAttestor != address(0), "SimpleAttestationVerifier: Zero address");

        address oldAttestor = zktlsAttestor;
        zktlsAttestor = _newAttestor;

        emit ZktlsAttestorUpdated(oldAttestor, _newAttestor);
    }

    /* ============ Internal Functions ============ */

    /**
     * @notice Verifies the attestation
     * @param _digest The message digest to verify (EIP-712 formatted)
     * @param _sigs Array with single signature from the witness
     * @return isValid True if attestation is valid
     */
    function _verifyAttestation(bytes32 _digest, bytes[] calldata _sigs) internal view returns (bool isValid) {
        address[] memory witnesses = new address[](1);
        witnesses[0] = witness;

        // Verify signatures meet threshold
        isValid = ThresholdSigVerifierUtils.verifyWitnessSignatures(
            _digest,
            _sigs,
            witnesses,
            MIN_WITNESS_SIGNATURES
        );

        // Only return isValid if it's true, otherwise library reverts
        return isValid;
    }

    /**
     * @notice Verifies the zkTLS attestor address (trust anchor)
     * @param _data Encoded zkTLS attestor address
     * @return isValid True if attestor is valid
     */
    function _verifyTrustAnchor(bytes calldata _data) internal view returns (bool isValid) {
        // Decode zkTLS attestor address from data
        address attestor = abi.decode(_data, (address));

        // Verify attestor is the registered zkTLS attestor
        if (attestor != zktlsAttestor) {
            return false;
        }

        return true;
    }
}

contracts/unifiedVerifier/UnifiedPayment.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import { BaseUnifiedPaymentVerifier } from "./BaseUnifiedPaymentVerifier.sol";
import { INullifierRegistry } from "../interfaces/INullifierRegistry.sol";
import { IPaymentVerifier } from "../interfaces/IPaymentVerifier.sol";
import { IAttestationVerifier } from "../interfaces/IAttestationVerifier.sol";

/**
 * @title UnifiedPaymentVerifier
 * @notice Verifies payment proofs for multiple payment methods. This is a unified verifier that 
 * replaces individual payment verifiers (VenmoVerifier, PayPalVerifier, etc.) with a single 
 * configurable contract.
 * 
 * Key features:
 * - Supports multiple payment methods, each with custom configuration
 * - Uses AttestationVerifier to validate off-chain zkTLS proofs and transformations
 * - Ensures trust anchor integrity for off-chain verification processes
 * - Verifies standardized payment details against provided data
 * @dev The payment attestation should be signed using the EIP-712 standard
 */
contract UnifiedPaymentVerifier is IPaymentVerifier, BaseUnifiedPaymentVerifier {

    /* ============ Constants ============ */

    // EIP-712 Domain Separator
    bytes32 private constant DOMAIN_TYPEHASH = keccak256(
        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
    );

    // EIP-712 Type Hash for PaymentAttestation
    bytes32 private constant PAYMENT_ATTESTATION_TYPEHASH = keccak256(
        "PaymentAttestation(bytes32 paymentMethod,bytes32 providerHash,bytes32 intentHash,bytes32 recipientId,uint256 amount,uint256 timestamp,bytes32 paymentId,bytes32 currency,bytes32 dataHash)"
    );

    /* ============ State Variables ============ */

    // EIP-712 Domain Separator (computed once at deployment)
    bytes32 public immutable DOMAIN_SEPARATOR;

    /* ============ Structs ============ */

    struct PaymentDetails {
        bytes32 recipientId;             // Payment recipient ID (hashed payee details to preserve privacy)
        uint256 amount;                  // Payment amount in smallest currency unit (i.e. cents)
        uint256 timestamp;               // Payment timestamp in UTC in milliseconds
        bytes32 paymentId;               // Hashed payment identifier from the service (e.g. hashed venmo payment ID)
        bytes32 currency;                // Currency hash (e.g., "USD", "EUR")
    }

    struct PaymentAttestation {
        bytes32 paymentMethod;           // Payment method hash (e.g., "venmo", "paypal", "wise")
        bytes32 providerHash;            // Unique identifier binding processor to provider
        bytes32 intentHash;              // Binds the payment to the intent on Orchestrator
        PaymentDetails paymentDetails;   // Payment details
        bytes[] signatures;              // Array of signatures from witnesses
        bytes32 dataHash;                // Hash of verification data for integrity
    }

    /* ============ Constructor ============ */

    constructor(
        address _escrow,
        INullifierRegistry _nullifierRegistry,
        IAttestationVerifier _attestationVerifier
    ) BaseUnifiedPaymentVerifier(
        _escrow,
        _nullifierRegistry,
        _attestationVerifier
    ) {
        // Compute EIP-712 domain separator
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                DOMAIN_TYPEHASH,
                keccak256(bytes("UnifiedPaymentVerifier")), // name
                keccak256(bytes("1")),                      // version
                block.chainid,                              // chainId
                address(this)                               // verifyingContract
            )
        );
    }

    /* ============ External Functions ============ */

    /**
     * ONLY ORCHESTRATOR: Verifies a standardized payment attestation generated by the attestation service.
     * 
     * @param _verifyPaymentData Payment proof and intent details required for verification
     * @return result The payment verification result containing success status, intent hash, release amount, 
     * payment currency and payment ID
     */
    function verifyPayment(
        VerifyPaymentData calldata _verifyPaymentData
    )
        external
        override
        onlyOrchestrator()
        returns (PaymentVerificationResult memory result)
    {
        PaymentAttestation memory attestation = abi.decode(
            _verifyPaymentData.paymentProof, 
            (PaymentAttestation)
        );

        PaymentMethodStore storage store = _getPaymentMethodStore(
            attestation.paymentMethod
        );

        // Verify the attestation and provider hash
        bool isValid = _verifyAttestation(attestation, _verifyPaymentData);
        require(isValid, "UPV: Invalid witness signatures");
        require(store.isProviderHash[attestation.providerHash], "UPV: Invalid provider hash");

        PaymentDetails memory paymentDetails = attestation.paymentDetails;
        _verifyPaymentDetails(paymentDetails, store, _verifyPaymentData);

        // Nullify the payment to prevent double-spending
        _nullifyPayment(attestation.paymentMethod, paymentDetails.paymentId);

        result = _createPaymentVerificationResult(
            _verifyPaymentData,
            attestation,
            paymentDetails
        );

        return result;
    }

    /* ============ Internal Functions ============ */

    /**
     * Verifies the EIP-712 attestation using the attestation verifier. Also verifies the integrity of the 
     * verify payment data using the data hash attached to the attestation.
     */
    function _verifyAttestation(
        PaymentAttestation memory attestation,
        VerifyPaymentData calldata _verifyPaymentData
    ) internal view returns (bool) {
        bytes32 structHash = keccak256(
            abi.encode(
                PAYMENT_ATTESTATION_TYPEHASH,
                attestation.paymentMethod,
                attestation.providerHash,
                attestation.intentHash,
                attestation.paymentDetails.recipientId,
                attestation.paymentDetails.amount,
                attestation.paymentDetails.timestamp,
                attestation.paymentDetails.paymentId,
                attestation.paymentDetails.currency,
                attestation.dataHash
            )
        );

        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR,
                structHash
            )
        );

        // Verify data integrity - the data hash must match what was signed
        require(
            keccak256(_verifyPaymentData.data) == attestation.dataHash,
            "UPV: Data hash mismatch"
        );

        bool isValid = attestationVerifier.verify(
            digest, 
            attestation.signatures,
            _verifyPaymentData.data
        );

        return isValid;
    }

    /**
     * Verifies the payment details. Verifies the receiver ID (payee), currency and timestamp against the verify payment data.
     */
    function _verifyPaymentDetails(
        PaymentDetails memory paymentDetails,
        PaymentMethodStore storage config,
        VerifyPaymentData calldata _verifyPaymentData
    ) internal view {
        require(
            paymentDetails.recipientId == _verifyPaymentData.payeeDetails,
            "UPV: Payee mismatch"
        );

        require(
            paymentDetails.currency == _verifyPaymentData.fiatCurrency,
            "UPV: Currency mismatch"
        );

        // Verify timestamp is after intent creation (with payment method-specific buffer for L2 flexibility)
        uint256 paymentTimestampWithBuffer = paymentDetails.timestamp + config.timestampBuffer;
        require(
            paymentTimestampWithBuffer >= _verifyPaymentData.intentTimestamp,
            "UPV: Payment before intent"
        );
    }

    /**
     * Nullifies a payment to prevent double-spending
     * @dev Creates a unique nullifier by encoding both the payment method and payment ID together.
     * This prevents collisions where the same payment ID could exist across different payment
     * methods (e.g., Venmo transaction #123 vs PayPal transaction #123).
     */
    function _nullifyPayment(bytes32 paymentMethod, bytes32 paymentId) internal {
        bytes32 nullifier = keccak256(abi.encodePacked(paymentMethod, paymentId));
        _validateAndAddNullifier(nullifier);
    }

    /**
     * Creates the release amount and creates the payment verification result to return to the Orchestrator.
     */
    function _createPaymentVerificationResult(
        VerifyPaymentData calldata _verifyPaymentData,
        PaymentAttestation memory attestation,
        PaymentDetails memory paymentDetails
    ) internal pure returns (PaymentVerificationResult memory result) {
        uint256 releaseAmount = _calculateReleaseAmount(
            paymentDetails.amount, 
            _verifyPaymentData.conversionRate, 
            _verifyPaymentData.intentAmount
        );

        result = PaymentVerificationResult({
            success: true,
            intentHash: attestation.intentHash,
            releaseAmount: releaseAmount
        });
    }
}
